import { fetchList, getObj, addObj, putObj, delObj } from '@/api/portal/concern/datasourceType'
import { tableOption,addsourceOption,listOption } from '@/const/crud/portal/concern/datasourceType'
import { mapGetters } from 'vuex'
export default {
    name: "datasourceType",
    data() {
        return {
            addsource:false,
            listsource:false,
            listOption:listOption,
            addOption:addsourceOption,
            option: tableOption,
            data: [
               {
                title: 'Oracle',
                text1: '25',
                img: 'https://gw.alipayobjects.com/zos/rmsportal/WdGqmHpayyMjiEhcKoVE.png'
            },
                {
                title: 'Mysql',
                text2: '25',
                img: 'https://gw.alipayobjects.com/zos/rmsportal/zOsKZmFRdUtvpqCImOVY.png'
            },
                {
                title: 'SQL Server',
                text3: '25',
                img: 'https://gw.alipayobjects.com/zos/rmsportal/dURIMkkrRFpPgTuzkwnB.png'
            }
            ]
        }
    },
    computed: {
        infoData() {
            return this.form.info
        }
    },
    methods:{
        submitForm(formName) {
            this.$refs[formName].validate(valid => {
                if (valid) {
                    request({
                        url: '/admin/user/edit',
                        method: 'put',
                        data: this.ruleForm2
                    }).then(response => {
                        if (response.data.data) {
                            this.$notify({
                                title: '成功',
                                message: '修改成功',
                                type: 'success',
                                duration: 2000
                            })
                            // 修改密码之后强制重新登录
                            if (this.switchStatus === 'passwordManager') {
                                this.$store.dispatch('LogOut').then(() => {
                                    location.reload() // 为了重新实例化vue-router对象 避免bug
                                })
                            }
                        } else {
                            this.$notify({
                                title: '失败',
                                message: response.data.msg,
                                type: 'error',
                                duration: 2000
                            })
                        }
                    }).catch(() => {
                        this.$notify({
                            title: '失败',
                            message: '修改失败',
                            type: 'error',
                            duration: 2000
                        })
                    })
                } else {
                    return false
                }
            })
        },
        resetForm(formName) {
            this.$refs[formName].resetFields()
        },

            tip(row, index) {
                this.$message.success('查看控制台')
                console.log(row, index)
            },

        handleOpt(row, index, done, loading){
            done();
            this.$notify({
                title: '成功',
                message: '操作成功',
                type: 'success',
                duration: 2000
            })
        },
        /**
         * @title 数据添加
         * @param row 为当前的数据
         * @param done 为表单关闭函数
         *
         **/
        handleSav: function (row, done) {
            addObj(row).then(data => {
                this.tableData.push(Object.assign({}, row))
                this.$message({
                    showClose: true,
                    message: '添加成功',
                    type: 'success'
                })
                this.getList(this.page)
                done()
            })
        },
        handleDelete() {
            var _this = this
            this.$confirm('是否确认删除该记录吗"?', '警告', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            }).then(function () {
                _this.$message({
                    showClose: true,
                    message: '删除成功',
                    type: 'success'
                })
            }).catch(function (err) {
            })
        },
    }
}
