result:
.info-cate {
    .fileInfo_main {
        box-shadow: 1px 1px 9px 8px #eee;
    }

    * {
        box-sizing: border-box;
    }

    .aljiang1 {
        color: #009944;
        font-weight: bold;
    }

    .aljiang {
        color: red;
        font-weight: bold;
    }

    .el-card__body {
        padding: 0px;
    }

    .el-col {
        margin-bottom: 0px;
    }

    .el-tag {
        margin-right: 10px !important;
    }

    .fileInfo_main {
        background: #fff;
        padding: 0 10px;
    }

    .form-main {
        overflow: hidden;
        font-size: 15px;

        .form-main-body {
            padding-left: 10px;
        }

        .form-main-title {
            height: 60px;
            line-height: 60px;
            font-size: 25px;
            color: #067af3;
            font-weight: bold;
            padding-left: 10px;

            .main-title-name {
                margin-right: 25px;
            }

            .main-title-score {
                color: #ff9913;
                background-color: #feedd5;
                padding: 5px 10px;
                font-size: 12px;
                margin-right: 10px;

            }

            .main-title-wsc {
                color: #CDD;
                padding: 5px 10px;
                font-size: 12px;
                margin-right: 10px;

            }

            .main-title-ysc {
                color: #ff9913;
                padding: 5px 10px;
                font-size: 12px;
                margin-right: 10px;

            }

        }

        .el-form-item {
            margin-bottom: 0px;
        }

        .el-icon-location-outline {
            color: #71a1e2;
            font-size: 18px;
            font-weight: bold;
        }

        .el-form-item__label {
            color: #7f7f7f;
        }

        .el-form-item__content {
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
            padding-right: 5px;
        }

        .form-body-r {
            img {
                display: block;
                width: 270px;
                min-width: 270px;
                height: 230px;
                min-height: 230px;
            }
        }
    }

    .card-tab {
        margin-top: 20px;
        min-height: 190px;
        overflow: hidden;
        -webkit-box-shadow: 0 0px 0px 0 rgba(0, 0, 0, .12), 0 0 0px 0 rgba(0, 0, 0, .04) !important;
        box-shadow: 0 0px 0px 0 rgba(0, 0, 0, .12), 0 0 0px 0 rgba(0, 0, 0, .04) !important;
        border-left: 1px solid #409eff;
        border-right: none;
        border-bottom: 1px solid #409eff;
        border-top: none;

        .el-tabs__header {
            background-color: #fff !important;
            border-bottom: 1px solid #409eff !important;
        }

        .el-tabs__item {
            border: 1px solid #409eff;
            margin: 0px -1px -1px;
            color: #303133;
            margin-right: 10px;
            float: left;
        }

        .is-active {
            border-right-color: #409eff !important;
            border-left-color: #409eff !important;
            border-top: 2px solid #409eff !important;
            border-bottom: none;
        }

        .el-tabs__content {
            padding: 15px;
            height: 190px;
            border-right: 1px solid #409eff;
        }

        ul {
            list-style: none;
            margin: 0;
            padding: 0;
            clear: both;
            overflow: hidden;
            width: 100%;

            li {
                float: left;
                font-size: 15px;
                color: #000;
                height: 30px;
                line-height: 30px;
            }

            li:nth-child(1) {
                width: 3%;
            }

            li:nth-child(2) {
                width: 8%;
                font-weight: bold;
            }

            li:nth-child(3) {
                width: 69%;
                color: #97baea;
                white-space: nowrap;
                overflow: hidden;
                text-overflow: ellipsis;
            }

            li:nth-child(4) {
                width: 20%;
                text-align: right;
                padding-right: 10px;
            }
        }
    }

    .table-box {
        margin-top: 20px;
        border: 1px solid #74a2de;

        .aljiantou-lv {
            font-size: 12px;
            color: #069b48;
        }

        .aljiantou-hong {
            font-size: 12px;
            color: #e60012;
        }

        .table-box-top {
            height: 40px;
            line-height: 40px;
            padding-left: 10px;
            border-bottom: 1px solid #74a2de;
            overflow: hidden;

            .box-top-item1 {
                font-weight: bold;
                color: #000000;

                span {
                    color: #44da8f;
                    font-size: 22px;
                    height: 40px;
                    line-height: 40px;
                    display: inline-block;
                    vertical-align: -3%;
                }
            }

            .box-top-item2, .box-top-item3 {
                font-size: 14px;
            }

            .box-top-item2 {
                span {
                    color: #000;
                    font-weight: bold;
                }
            }

            .box-top-item3 {
                span {
                    color: #000;
                    font-weight: bold;
                }
            }

            .box-top-item4 {
                text-align: right;
                padding-right: 20px;
                color: #1f94f4;
                font-size: 15px;
            }
        }
    }

    .table-place {
        margin-top: 20px;
        height: 315px;
        overflow: hidden;
        max-height: 360px;
        border-bottom: 1px solid #74a2de;
        position: relative;

        .iconfontlogo {
            font-size: 40px;
        }

        .almore {
            font-size: 20px;
        }

        .el-tabs--border-card {
            border: none;
            -webkit-box-shadow: 0 0px 0px 0 rgba(0, 0, 0, .12), 0 0 0px 0 rgba(0, 0, 0, .04);
            box-shadow: 0 0px 0px 0 rgba(0, 0, 0, .12), 0 0 0px 0 rgba(0, 0, 0, .04);
        }

        .table-place-item {
            margin-right: 40px;
            overflow: hidden;
            padding-right: 0px !important;
        }

        .table-place-more {
            width: 40px;
            border-right: 1px solid #74a2de;
            padding-left: 0px !important;
            padding-right: 0px !important;
            height: 360px;
            text-align: center;
            border-top: 1px solid #409eff;
            border-bottom: 1px solid #409eff;
            border-left: 1px solid #409eff;
            max-width: 40px;
            right: 0;
            top: 0;
            position: absolute;

            .el-button {
                height: 360px;
            }
        }

        .el-tabs__active-bar {
            background-color: #fff !important;
        }

        .el-tabs__item {
            width: 150px;
            padding-left: 0px !important;
            padding-right: 0px !important;
            height: 70px;
            overflow: hidden;
            margin-bottom: 10px;
        }

        .el-tabs__item + .el-tabs__item {
            margin-bottom: 10px;
        }

        .el-tabs__item:first-child {
            margin-bottom: 10px;
        }

        .el-tabs__item:last-child {
            margin-bottom: 0px;
        }

        .el-tabs--border-card > .el-tabs__header {
            background: #fff;
            border-bottom: none;
        }

        .el-tabs--left .el-tabs__item.is-left {
            text-align: left;
            border: 1px solid #409eff;
            margin: 0px -1px 15px 1px;
            width: 180px;
            height: 95px;
            min-width: 180px;
            min-height: 95px;
        }

        .el-tabs--left .el-tabs__item.is-left:last-child {
            margin: 0px -1px 0px 1px;
        }

        .el-tabs--left .el-tabs__header.is-left {
            margin-right: 0px;
            border-right: 1px solid #409eff;
            height: 315px;
            width: 180px;
        }

        .el-tabs__item {
            color: #fff;
            background: #74a2de;
        }

        .is-active {
            color: #74a2de;
            background: #FFF;
            border-color: #409eff transparent;
        }

        .place-tab-body-icon {
            height: 95px;
            line-height: 95px;
            font-size: 30px;
            text-align: center;
        }

        .place-tab-body-content {
            text-align: right;
            padding-right: 10px;

            h5 {
                margin: 0;
                padding: 0;
                height: 40px;
                line-height: 40px;
                font-size: 18px;
            }

            p {
                margin: 0;
                padding: 0;
                font-size: 25px;
            }
        }

        .el-tabs--border-card > .el-tabs__content {
            padding: 0px;
            border-top: 1px solid #409eff;
        }

        .place-body {
            padding-left: 25px;
        }
    }

    .table-face {
        margin-top: 20px;

        .table-face-body {
            border: 1px solid #74a2de;
        }

        .table-face-top {
            color: #74a2de;
            font-size: 16px;
            font-weight: bold;
            height: 40px;
            line-height: 40px;

            .face-top-more {
                text-align: right;
                padding-right: 20px;

            }
        }
    }

    .map {
        .map_box {
            height: 400px;
        }
    }
}
