import {fetchDictTypeList, addObj, getObj, delObj, putObj} from '@/api/admin/dict-type'
import {tableOption} from '@/const/crud/admin/dict-type'
import {mapGetters} from 'vuex'
export default {
    name: "dictType",
    data() {
        return {
            tableData: [],
            page: {
                total: 0, // 总页数
                currentPage: 1, // 当前页数
                pageSize: 20 // 每页显示多少条
            },
            tableLoading: false,
            tableOption: tableOption
        }
    },
    created() {
    },
    computed: {
        ...mapGetters(['permissions'])
    },
    methods: {
        getList(page, params) {
            this.tableLoading = true
            fetchDictTypeList(Object.assign({
                current: page.currentPage,
                size: page.pageSize
            }, params)).then(response => {
                this.tableData = response.data.data.records
                this.page.total = response.data.data.total
                this.tableLoading = false
            })
        },
        handleDel(row, index) {
            this.$refs.crud.rowDel(row, index)
        },
        refreshChange({page, searchForm}){
            let params = this.getParams(searchForm);
            this.getList(page, params)
        },
        rowDel: function (row, index) {
            var _this = this
            this.$confirm('是否确认删除字典类型为"' + row.innerFlag + '",字典名称为"' + row.chineseName + '"的数据项?', '警告', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            }).then(function () {
                return delObj(row)
            }).then(() => {
                this.getList(this.page)
                _this.$message({
                    showClose: true,
                    message: '删除成功',
                    type: 'success'
                })
            }).catch(function () {
            })
        },
        /**
         * @title 数据更新
         * @param row 为当前的数据
         * @param index 为当前更新数据的行数
         * @param done 为表单关闭函数
         *
         **/
        handleUpdate: function (row, index, done) {
            putObj(row).then(() => {
                this.tableData.splice(index, 1, Object.assign({}, row))
                this.$message({
                    showClose: true,
                    message: '修改成功',
                    type: 'success'
                })
                this.getList(this.page)
                done()
            }).catch(function (error) {
                console.log(error);
            })
        },
        /**
         * @title 数据添加
         * @param row 为当前的数据
         * @param done 为表单关闭函数
         *
         **/
        handleSave: function (row, done) {
            addObj(row).then(data => {
                this.tableData.push(Object.assign({}, row))
                this.$message({
                    showClose: true,
                    message: '添加成功',
                    type: 'success'
                })
                this.getList(this.page)
                done()
            })
        },
        getParams(form){
            let params ={
                innerFlag: undefined,
                chineseName: undefined,
                isTree: undefined
            }
            if(form.innerFlag != "")
                params.innerFlag = form.innerFlag
            if(form.chineseName != "")
                params.chineseName = form.chineseName
            if(form.isTree != "")
                params.isTree = form.isTree
            return params;
        },
        searchChange(form) {
            let params = this.getParams(form);
            this.getList(this.page, params)
        },
        handleEdit(row, index) {
            this.$refs.crud.rowEdit(row, index)
        },
        handleDictDetail(row, index){
            console.log(3333)
            this.$router.push({
                path: this.$router.$avueRouter.getPath({
                    name: "字典项",
                    src: "/admin/dict"
                }),
                query: {type: row.innerFlag, isTree: row.isTree}
            });
        }
    }
};
