mapper.select:
select * from t_thcs_analy_jx_btcsdcxg t


        AND t.car_no like '%'|| #{query.carNo}||'%'


        AND t.type = #{query.type}


        AND t.name like '%'|| #{query.name}||'%'


        AND to_char(t.repair_time, 'YYYY-mm') = #{query.repairMonth}


order by t.repair_time desc

select b.*,
DECODE(to_char(b.min_repair_time, 'yyyy-mm-dd'), to_char(b.repair_time, 'yyyy-mm-dd'), to_char(b.repair_time, 'yyyy-mm-dd'), to_char(b.min_repair_time, 'yyyy-mm-dd') || ' è‡³ ' || to_char(b.repair_time, 'yyyy-mm-dd')) as repairMonth
to_char(b.repair_time,'yyyy-mm') as repairMonth
from (select t.car_no as car_no, max(t.name) as name, max(t.type) as type, max(t.engine_no) as engine_no, max(t.car_type) as car_type, max(t.car_color) as car_color, max(t.repair_time) as repair_time, min(t.repair_time) as min_repair_time, max(t.idcard) as idcard, max(t.place_name) as place_name, max(t.dept_code) as dept_code, max(t.city_code) as city_code, max(t.place_id) as place_id, max(t.address) as address, max(t.monitor_time) as monitor_time, count(1) as sum from t_thcs_analy_jx_btcsdcxg t
AND t.car_no like '%'|| #{query.carNo}||'%'
AND t.type = #{query.type}
AND t.name like '%'|| #{query.name}||'%'
AND to_char(t.repair_time, 'yyyy-mm-hh') > #{query.startTime}
AND to_char(t.repair_time, 'yyyy-mm-hh') < #{query.endTime}
${query.dataScope}
group by t.car_no) b

select b.*,to_char(b.repair_time,'yyyy-mm') as repairMonth from (select t.car_no as car_no, max(t.name) as name, max(t.type) as type, max(t.engine_no) as engine_no, max(t.car_type) as car_type, max(t.car_color) as car_color, max(t.repair_time) as repair_time, min(t.repair_time) as min_repair_time, max(t.idcard) as idcard, max(t.place_name) as place_name, max(t.dept_code) as dept_code, max(t.city_code) as city_code, max(t.place_id) as place_id, max(t.address) as address, max(t.monitor_time) as monitor_time, to_char(t.repair_time, 'YYYY-mm') as month, count(1) as sum from t_thcs_analy_jx_btcsdcxg t
AND t.car_no like '%'|| #{query.carNo}||'%'
AND t.type = #{query.type}
AND t.name like '%'|| #{query.name}||'%'
AND to_char(t.repair_time, 'yyyy-mm-hh') > #{query.startTime}
AND to_char(t.repair_time, 'yyyy-mm-hh') < #{query.endTime}
${query.dataScope}
group by t.car_no, to_char(t.repair_time, 'YYYY-mm')) b order by b.month desc
